import ctypes
class Node:
    def __init__(self, value):
        self.value = value
        self.npx = 0
class XorLinkedList:
    def __init__(self):
        self.head = None
        self.tail = None
        self.__nodes = []
    def InsertAtStart(self, value):
        node = Node(value)
        if self.head is None:
            self.head = node
            self.tail = node
        else:
            self.head.npx = id(node) ^ self.head.npx
            node.npx = id(self.head)
            self.head = node
        self.__nodes.append(node)
    def InsertAtEnd(self, value):
        node = Node(value)
        if self.head is None:
            self.head = node
            self.tail = node
        else:
            self.tail.npx = id(node) ^ self.tail.npx
            node.npx = id(self.tail)
            self.tail = node
        self.__nodes.append(node)
    def DeleteAtStart(self):
        if self.isEmpty():
            return "List is empty !"
        elif self.head == self.tail:
            self.head = self.tail = None
        elif (0 ^ self.head.npx) == id(self.tail):
            self.head = self.tail
            self.head.npx = self.tail.npx = 0
        else:
            res = self.head.value
            x = self.__type_cast(0 ^ self.head.npx)
            y = (id(self.head) ^ x.npx)
            self.head = x
            self.head.npx = 0 ^ y
            return res
    def DeleteAtEnd(self):
        if self.isEmpty():
            return "List is empty !"
        elif self.head == self.tail:
            self.head = self.tail = None
        elif self.__type_cast(0 ^ self.head.npx) == (self.tail):
            self.tail = self.head
            self.head.npx = self.tail.npx = 0
        else:
            prev_id = 0
            node = self.head
            next_id = 1
            while next_id:
                next_id = prev_id ^ node.npx
                if next_id:
                    prev_id = id(node)
                    node = self.__type_cast(next_id)
            res = node.value
            x = self.__type_cast(prev_id).npx ^ id(node)
            y = self.__type_cast(prev_id)
            y.npx = x ^ 0
            self.tail = y
            return res
    def Print(self):
        if self.head != None:
            prev_id = 0
            node = self.head
            next_id = 1
            print(node.value, end=' ')
            while next_id:
                next_id = prev_id ^ node.npx
                if next_id:
                    prev_id = id(node)
                    node = self.__type_cast(next_id)
                    print(node.value, end=' ')
                else:
                    return
        else:
            print("List is empty !")
    def ReversePrint(self):
        if self.head != None:
            prev_id = 0
            node = self.tail
            next_id = 1
            print(node.value, end=' ')
            while next_id:
                next_id = prev_id ^ node.npx
                if next_id:
                    prev_id = id(node)
                    node = self.__type_cast(next_id)
                    print(node.value, end=' ')
                else:
                    return
        else:
            print("List is empty !")
    def Length(self):
        if not self.isEmpty():
            prev_id = 0
            node = self.head
            next_id = 1
            count = 1
            while next_id:
                next_id = prev_id ^ node.npx
                if next_id:
                    prev_id = id(node)
                    node = self.__type_cast(next_id)
                    count += 1
                else:
                    return count
        else:
            return 0
    def PrintByIndex(self, index):
        prev_id = 0
        node = self.head
        for i in range(index):
            next_id = prev_id ^ node.npx
            if next_id:
                prev_id = id(node)
                node = self.__type_cast(next_id)
            else:
                return "Value dosn't found index out of range."
        return node.value
    def isEmpty(self):
        if self.head is None:
            return True
        return False
    def __type_cast(self, id):
        return ctypes.cast(id, ctypes.py_object).value
