class Node:
    def __init__(self, data):
        self.data = data
        self.next = None
class CircularLinkedList:
    def __init__(self):
        self.head = Node(None)
        self.tail = Node(None)
        self.head.next = self.tail
        self.tail.next = self.head
    def add(self, data):
        newNode = Node(data)
        if self.head.data is None:
            self.head = newNode
            self.tail = newNode
            newNode.next = self.head
        else:
            self.tail.next = newNode
            self.tail = newNode
            self.tail.next = self.head
    def findNode(self, element):
        current = self.head
        f = 0
        if self.head == None:
            print("Empty list")
        else:
            while True:
                if current.data == element:
                    f += 1
                    break
                current = current.next
                if current == self.head:
                    break
            print("element is present" if f > 0 else "element is not present")
