void window_pattern(int n) {
    int c = (n % 2 != 0) ? (n / 2) + 1 : 0;
    int d = (n % 2 == 0) ? n / 2 : 0;
    for(int i = 1; i <= n; i++) {
        for(int j = 1; j <= n; j++) {
            if (i == 1 || j == 1 || i == n || j == n) cout << "* ";
            else if (i == c || j == c || i == d || j == d) cout << "* ";
            else cout << "  ";
        }
        cout << '\n';
    }
}
int main() {
    int n = 7;
    window_pattern(n);
    return 0;
}

