tree = None
while True:
    do = input('What would you like to do? ').split()
    operation = do[0].strip().lower()
    if operation == 'add':
        data = int(do[1])
        new_node = {'key': data, 'children': []}
        suboperation = do[2].strip().lower()
        if suboperation == 'at':
            tree = new_node
        elif suboperation == 'below':
            position = do[3].strip().lower()
            key = int(position)
            ref_node = None
            if tree is not None:
                ref_node = tree
                def search(node, key):
                    if node['key'] == key:
                        return node
                    for child in node['children']:
                        temp = search(child, key)
                        if temp is not None:
                            return temp
                    return None
                ref_node = search(tree, key)
            if ref_node is None:
                print('No such key.')
                continue
            ref_node['children'].append(new_node)
    elif operation == 'count':
        if tree is None:
            print('Tree is empty.')
        else:
            def count_leaf_nodes(node):
                if node['children'] == []:
                    return 1
                else:
                    count = 0
                    for child in node['children']:
                        count += count_leaf_nodes(child)
                    return count
            count = count_leaf_nodes(tree)
            print('Number of leaf nodes: {}'.format(count))
    elif operation == 'quit':
        break

