while True:
    do = input('What would you like to do? ').split()
    if do[0] == 'add':
        if do[1] == 'vertex':
            g.add_vertex(int(do[2]))
        elif do[1] == 'edge':
            src, dest, weight = int(do[2]), int(do[3]), int(do[4])
            g.vertices[src].points_to.update({g.vertices[dest]: weight})
            g.vertices[dest].points_to.update({g.vertices[src]: weight})
    elif do[0] == 'mst':
        mst = type('Graph', (), {'vertices': {}})()
        unvisited = set(g.vertices.values())
        u = next(iter(unvisited))
        mst.add_vertex(u.key)
        unvisited.remove(u)
        nearest_neighbour = {n: mst.vertices[u.key] for n in u.points_to if n != u}
        smallest_distance = {n: u.points_to[n] for n in u.points_to if n != u}
        while smallest_distance:
            outside_mst = min(smallest_distance, key=smallest_distance.get)
            inside_mst = nearest_neighbour[outside_mst]
            mst.add_vertex(outside_mst.key)
            mst.vertices[outside_mst.key].points_to.update({inside_mst: smallest_distance[outside_mst]})
            mst.vertices[inside_mst.key].points_to.update({outside_mst: smallest_distance[outside_mst]})
            unvisited.remove(outside_mst)
            del smallest_distance[outside_mst]
            del nearest_neighbour[outside_mst]
            for n in outside_mst.points_to:
                if n in unvisited:
                    if n not in smallest_distance or smallest_distance[n] > outside_mst.points_to[n]:
                        smallest_distance[n] = outside_mst.points_to[n]
                        nearest_neighbour[n] = mst.vertices[outside_mst.key]
        print('Minimum Spanning Tree:')
        for v in mst.vertices.values():
            for dest, weight in v.points_to.items():
                print('(src={}, dest={}, weight={})'.format(v.key, dest.key, weight))
    elif do[0] == 'display':
        for v in g.vertices.values():
            for dest, weight in v.points_to.items():
                print('(src={}, dest={}, weight={})'.format(v.key, dest.key, weight))
    elif do[0] == 'quit':
