        a = fr * val * fact(n-1) / fact(v-1) / fact(l1) / fact(l2) * p1 * p2 / fact(e)
        ret += a
  elif t==1:
    n=Y[1]
    ret=0
    for (v,e) in sve:
      val=sve[(v,e)]
      e-=1
      if e==-1 or v+e>n or v==0:continue
      for l1 in range(n-v+1):
        l2=n-v-l1
        p1=ff((2,l1,e))
        p2=ff((3,l2,v))
        ret += fr * val * fact(n) / fact(v) / fact(l1) / fact(l2) * p1 * p2 / fact(e)
  elif t==2:
    n=Y[1]
    e=Y[2]
    F=[ fr*i*ff((0,i))/fact(i) for i in range(n+1) ]
    Fa=powpoly(F,e)
    ret=Fa[n]*fact(n)
  elif t==3:
    n=Y[1]
    v=Y[2]
    G=[v*fr*ff((1,i))/fact(i) for i in range(n+1)]
    Ga=ing(G)
    ret = Ga[n]*fact(n)
  memo_ff[Y]=ret
  return ret

memo={}

def g(Y):
  if Y in memo:
    return memo[Y]
  k,c=Y
  if c==0:
    return ff((0,k))
  if 2*c>=k:return 0
  ret=0
  for k1 in range(1,18):
    for k2 in range(1,18):
      k3=k-k1-k2
      if k3<=0:break
      for c1 in range(18):
        if 2*c1>=k1:break
        for c2 in range(18):
          if 2*c2>=k2:break
          c3=c-1-c1-c2
          if 2*c3>=k3:continue
          ret += g((k1,c1)) * g((k2,c2)) * g((k3,c3)) * fact(k1+k2+k3)/fact(k1)/fact(k2)/fact(k3)*k1*k2*k3
  r=ret/(6*c)
  memo[Y]=r
  return r


